INSERT INTO hotel_booking (city, hotel_name, number_of_people, booking_start,booking_end) VALUES
#{hotelBooking.city}, #{hotelBooking.hotelName}, #{hotelBooking.numberOfPeople}, #{hotelBooking.bookingStart}, #{hotelBooking.bookingEnd}
ON DUPLICATE KEY UPDATE city = VALUES(city), hotel_name = VALUES(hotel_name), number_of_people = VALUES(number_of_people), booking_start = VALUES(booking_start), booking_end = VALUES(booking_end)
